var ComponentsDropdowns = function () {
    var l = function () {
        $("#my_multi_select1").multiSelect();

        $('.multi-select-all').click(function () {
            $('#my_multi_select1').multiSelect('select_all');
            return false;
        });
        $('.multi-select-null').click(function () {
            $('#my_multi_select1').multiSelect('deselect_all');
            return false;
        });
        $('.multi-select-reset').on('click', function () {
            $('#my_multi_select1').multiSelect('deselect_all');
            var elem_list = $(this).attr('elem').split(',');
            $('#my_multi_select1').multiSelect('select', elem_list);
            return false;
        });
    };
    return {
        init: function () {
            l()
        }
    }
}();
jQuery(document).ready(function () {
    ComponentsDropdowns.init()
});